INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_001', 'Единственный способ сделать что-то хорошо — полюбить то, что вы делаете.', 'Стив Джобс', 'Мотивация', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_002', 'Жизнь — это то, что происходит с вами, пока вы строите другие планы.', 'Джон Леннон', 'Жизнь', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_003', 'Путь в тысячу миль начинается с первого шага.', 'Лао-цзы', 'Начинания', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_004', 'Не важно, как медленно вы идете, главное — не останавливаться.', 'Конфуций', 'Настойчивость', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_005', 'Счастье — это не цель, а способ жить.', 'Далай-лама', 'Счастье', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_006', 'Будьте тем изменением, которое хотите видеть в мире.', 'Махатма Ганди', 'Вдохновение', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_007', 'Лучшее время посадить дерево было 20 лет назад. Второе лучшее время — сейчас.', 'Китайская пословица', 'Действие', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_008', 'Успех — это способность идти от неудачи к неудаче, не теряя энтузиазма.', 'Уинстон Черчилль', 'Успех', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_009', 'Ваше время ограничено, не тратьте его на жизнь чужой жизнью.', 'Стив Джобс', 'Аутентичность', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_010', 'Единственная невозможная вещь — это та, которую вы не пытались сделать.', 'Неизвестный автор', 'Возможности', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_011', 'Падать — это нормально. Подниматься — обязательно.', 'Конфуций', 'Стойкость', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_012', 'Мудрость приходит с опытом, а опыт — с ошибками.', 'Оскар Уайльд', 'Мудрость', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_013', 'Сила не в том, чтобы никогда не падать, а в том, чтобы подниматься каждый раз.', 'Нельсон Мандела', 'Стойкость', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_014', 'Каждый день — новый шанс изменить свою жизнь.', 'Неизвестный автор', 'Начинания', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_015', 'Когда вы говорите «да» другим, убедитесь, что не говорите «нет» себе.', 'Пауло Коэльо', 'Баланс', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_016', 'Вдохновение существует, но оно приходит во время работы.', 'Пабло Пикассо', 'Творчество', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_017', 'Чтобы быть незаменимым, нужно быть неповторимым.', 'Коко Шанель', 'Аутентичность', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_018', 'Люди видят мир не таким, какой он есть, а такими, какие они сами.', 'Альбер Камю', 'Осознанность', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_019', 'Свобода — это ответственность.', 'Жан-Поль Сартр', 'Ответственность', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_020', 'Если хочешь иметь то, чего никогда не имел, сделай то, чего никогда не делал.', 'Томас Джефферсон', 'Действие', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_021', 'Мечты сбываются у тех, кто их преследует.', 'Неизвестный автор', 'Мотивация', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_022', 'Если проблема решаема, то не стоит о ней беспокоиться. Если не решаема — беспокоиться бесполезно.', 'Далай-лама', 'Мудрость', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_023', 'Характер формируется не в легкие времена, а в испытаниях.', 'Неизвестный автор', 'Стойкость', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_024', 'Никогда не поздно стать тем, кем ты мог бы быть.', 'Джордж Элиот', 'Начинания', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_025', 'Куда бы ты ни шел, иди всем сердцем.', 'Конфуций', 'Осознанность', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_026', 'Сомнения убивают больше мечтаний, чем неудачи.', 'Сьюзи Кассем', 'Возможности', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_027', 'Мы становимся тем, о чем думаем большую часть времени.', 'Эрл Найтингейл', 'Мышление', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_028', 'Победа — это состояние духа.', 'Неизвестный автор', 'Успех', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_029', 'Не сравнивай себя ни с кем. Сравни себя с собой вчерашним.', 'Неизвестный автор', 'Саморазвитие', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_030', 'Быть счастливым не значит иметь всё, что хочешь, а значит уметь радоваться тому, что есть.', 'Неизвестный автор', 'Счастье', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_031', 'Ошибки — это доказательство того, что вы пытаетесь.', 'Неизвестный автор', 'Мотивация', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_032', 'Настоящее богатство — это возможность жить так, как хочется.', 'Генри Дэвид Торо', 'Свобода', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_033', 'Когда ты изменишься, все вокруг изменится.', 'Неизвестный автор', 'Осознанность', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_034', 'Человек, который никогда не совершал ошибок, никогда не пробовал ничего нового.', 'Альберт Эйнштейн', 'Творчество', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_035', 'Спокойствие — это не отсутствие эмоций, а управление ими.', 'Неизвестный автор', 'Осознанность', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_036', 'Уважайте себя настолько, чтобы уйти от всего, что вам не служит.', 'Неизвестный автор', 'Самоуважение', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_037', 'Мы не можем контролировать ветер, но можем настроить паруса.', 'Неизвестный автор', 'Гибкость', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_038', 'Иногда нужно отойти в сторону, чтобы увидеть дорогу впереди.', 'Неизвестный автор', 'Осознанность', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_039', 'Там, где есть любовь, есть жизнь.', 'Махатма Ганди', 'Любовь', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_040', 'Ваши мысли определяют вашу реальность.', 'Неизвестный автор', 'Мышление', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_041', 'Каждый человек — архитектор своей судьбы.', 'Аппий Клавдий', 'Ответственность', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_042', 'Счастье — это направление, а не место.', 'Сидни Харрис', 'Счастье', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_043', 'У вас есть все, что нужно, чтобы справиться с любым вызовом.', 'Неизвестный автор', 'Саморазвитие', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_044', 'Учитесь отдыхать, а не сдаваться.', 'Неизвестный автор', 'Баланс', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_045', 'Ваша сила — в вашем выборе.', 'Неизвестный автор', 'Ответственность', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_046', 'Не бойтесь говорить «нет», если это охраняет ваш внутренний мир.', 'Неизвестный автор', 'Границы', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_047', 'Любое путешествие начинается с решения сделать первый шаг.', 'Неизвестный автор', 'Начинания', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_048', 'Тише едешь — дальше будешь.', 'Русская пословица', 'Осознанность', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_049', 'Если хочешь идти быстро — иди один. Если хочешь идти далеко — идите вместе.', 'Африканская пословица', 'Отношения', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_050', 'Мы привлекаем то, чем являемся.', 'Неизвестный автор', 'Мышление', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_051', 'Видимая сила — это отражение внутренней силы.', 'Неизвестный автор', 'Саморазвитие', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_052', 'Любите себя за путь, а не только за достижения.', 'Неизвестный автор', 'Самоуважение', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_053', 'Умение слушать — это начало понимания.', 'Неизвестный автор', 'Осознанность', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_054', 'Настоящий успех — это умение быть довольным собой.', 'Неизвестный автор', 'Успех', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_055', 'Не ждите идеального момента. Сделайте момент идеальным.', 'Неизвестный автор', 'Мотивация', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_056', 'Терпение — это не умение ждать, а умение сохранять хорошее настроение в ожидании.', 'Неизвестный автор', 'Настойчивость', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_057', 'Улыбка — это универсальный ключ к добрым отношениям.', 'Неизвестный автор', 'Отношения', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_058', 'Чтобы изменить мир, начни с себя.', 'Лев Толстой', 'Вдохновение', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_059', 'Жизнь начинается там, где заканчивается зона комфорта.', 'Нил Доналд Уолш', 'Начинания', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_060', 'Вы не можете вернуться и изменить начало, но можете начать сейчас и изменить конец.', 'К. С. Льюис', 'Начинания', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_061', 'Иногда молчание — лучший ответ.', 'Далай-лама', 'Осознанность', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_062', 'Забота о себе — это не эгоизм, а необходимость.', 'Неизвестный автор', 'Забота о себе', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_063', 'Кто хочет — ищет возможности, кто не хочет — ищет причины.', 'Аристотель', 'Ответственность', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_064', 'Мыслим — значит существуем.', 'Рене Декарт', 'Мышление', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_065', 'Смелость — это мастерство начать.', 'Неизвестный автор', 'Смелость', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_066', 'Кратчайший путь к себе — через честность.', 'Неизвестный автор', 'Аутентичность', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_067', 'Цени моменты, они никогда не повторятся.', 'Неизвестный автор', 'Осознанность', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_068', 'Когда одна дверь закрывается, открывается другая.', 'Александр Белл', 'Надежда', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_069', 'Секрет счастья — в маленьких радостях.', 'Неизвестный автор', 'Счастье', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_070', 'Не смотри назад — ты туда не идешь.', 'Неизвестный автор', 'Мотивация', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_071', 'Делайте то, что можете, с тем, что имеете, там, где вы есть.', 'Теодор Рузвельт', 'Ответственность', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_072', 'Сила улыбки в том, что она может изменить день.', 'Неизвестный автор', 'Счастье', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_073', 'Тот, кто светит, никогда не оставляет других в темноте.', 'Неизвестный автор', 'Вдохновение', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_074', 'В тысяче миль пути самое важное — первый шаг.', 'Неизвестный автор', 'Начинания', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_075', 'Настоящий друг — тот, кто видит боль в твоих глазах, когда все верят твоей улыбке.', 'Неизвестный автор', 'Отношения', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_076', 'Будь таким человеком, которого хотел бы встретить.', 'Неизвестный автор', 'Саморазвитие', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_077', 'Всё, что мы есть — результат наших мыслей.', 'Будда', 'Мышление', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_078', 'Благодарность превращает то, что мы имеем, в достаток.', 'Неизвестный автор', 'Благодарность', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_079', 'Удивительное начинается там, где заканчивается привычное.', 'Неизвестный автор', 'Творчество', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_080', 'Ты сильнее, чем тебе кажется.', 'Неизвестный автор', 'Сила', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_081', 'Научись слушать тишину, и ты услышишь себя.', 'Неизвестный автор', 'Осознанность', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_082', 'Не сравнивайте свое начало с серединой чужого пути.', 'Неизвестный автор', 'Саморазвитие', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_083', 'Иногда лучшая реакция — ничто.', 'Неизвестный автор', 'Границы', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_084', 'Доверяйте процессу, даже если сейчас он непонятен.', 'Неизвестный автор', 'Надежда', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_085', 'Тишина учит нас слушать собственное сердце.', 'Неизвестный автор', 'Осознанность', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_086', 'В спокойствии есть сила.', 'Неизвестный автор', 'Сила', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_087', 'Будь мягким, но не позволяй себя ломать.', 'Неизвестный автор', 'Границы', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_088', 'Отпусти то, что не дает расти.', 'Неизвестный автор', 'Освобождение', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_089', 'Ищите свет внутри себя, и вы найдёте его в мире.', 'Неизвестный автор', 'Вдохновение', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_090', 'Покой — это высшая форма счастья.', 'Неизвестный автор', 'Счастье', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_091', 'Когда твое сердце полно, оно само знает путь.', 'Неизвестный автор', 'Осознанность', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_092', 'Не торопи себя. У каждого цветка своё время расцвести.', 'Неизвестный автор', 'Терпение', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_093', 'Доброта — язык, который понятен каждому.', 'Неизвестный автор', 'Отношения', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_094', 'Вера в себя превращает невидимое в видимое.', 'Неизвестный автор', 'Саморазвитие', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_095', 'Движение вперед начинается с честного взгляда внутрь.', 'Неизвестный автор', 'Осознанность', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_096', 'Не бойся изменений — они приводят к росту.', 'Неизвестный автор', 'Начинания', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_097', 'Ты — автор истории своей жизни. Пиши её осознанно.', 'Неизвестный автор', 'Ответственность', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_098', 'То, что ты ищешь, тоже ищет тебя.', 'Руми', 'Надежда', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_099', 'Всё, что приходит, — к лучшему, даже если сейчас это неочевидно.', 'Неизвестный автор', 'Надежда', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_100', 'Умение отдыхать — часть умения достигать.', 'Неизвестный автор', 'Баланс', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_101', 'У каждого шторма есть конец.', 'Неизвестный автор', 'Надежда', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_102', 'Быть собой — значит быть свободным.', 'Неизвестный автор', 'Аутентичность', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_103', 'Внутренняя тишина — источник силы.', 'Неизвестный автор', 'Сила', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_104', 'Сохраняй спокойствие. Ты уже пережил 100% своих худших дней.', 'Неизвестный автор', 'Стойкость', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_105', 'Слова лечат, когда они искренни.', 'Неизвестный автор', 'Отношения', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_106', 'Поддерживайте тех, кто поддерживает вас.', 'Неизвестный автор', 'Отношения', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_107', 'В каждом дне есть что-то хорошее. Найдите это.', 'Неизвестный автор', 'Благодарность', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_108', 'Смелость — это страх, который прочитал молитву.', 'Неизвестный автор', 'Смелость', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_109', 'Уберите из жизни шум, и услышите свою мудрость.', 'Неизвестный автор', 'Осознанность', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_110', 'Выбирайте людей, рядом с которыми становится легко дышать.', 'Неизвестный автор', 'Отношения', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_111', 'Никто не сможет полить ваш сад, если вы сами не возьмётесь за лейку.', 'Неизвестный автор', 'Саморазвитие', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_112', 'Принятие — это дверь к спокойствию.', 'Неизвестный автор', 'Осознанность', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_113', 'Когда вы научитесь выбирать себя, вы перестанете бояться одиночества.', 'Неизвестный автор', 'Самоуважение', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_114', 'Смена перспективы приносит новые решения.', 'Неизвестный автор', 'Мышление', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_115', 'Побеждает тот, кто победил себя.', 'Неизвестный автор', 'Саморазвитие', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_116', 'Жизнь — это не ожидание бурь, а умение танцевать под дождем.', 'Вивьен Грин', 'Баланс', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_117', 'Отпусти ожидания, и ты увидишь реальность.', 'Неизвестный автор', 'Осознанность', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_118', 'Не позволяй вчерашним ошибкам жить в твоём сегодня.', 'Неизвестный автор', 'Прошлое', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_119', 'Сострадание начинается с себя.', 'Неизвестный автор', 'Забота о себе', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_120', 'Слышать сердцем — значит чувствовать глубже.', 'Неизвестный автор', 'Осознанность', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_121', 'Любовь к себе — самый верный компас.', 'Неизвестный автор', 'Самоуважение', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_122', 'Насколько далеко вы уйдёте, зависит от того, что вы решите оставить позади.', 'Неизвестный автор', 'Освобождение', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_123', 'Там, где есть жизнь, есть надежда.', 'Маркус Туллий Цицерон', 'Надежда', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_124', 'Умейте делать паузы между мыслями — там живёт ясность.', 'Неизвестный автор', 'Осознанность', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_125', 'Свет в конце тоннеля — не мираж, если ты продолжаешь идти.', 'Неизвестный автор', 'Настойчивость', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_126', 'Сила характера — в мягкости сердца.', 'Неизвестный автор', 'Саморазвитие', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_127', 'Чтобы прийти куда-то, нужно перестать бояться идти.', 'Неизвестный автор', 'Смелость', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_128', 'Люби усилие, а не результат.', 'Неизвестный автор', 'Мышление', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_129', 'Доверься времени, оно лечит лучше любых слов.', 'Неизвестный автор', 'Надежда', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_130', 'Вы — то, что вы делаете каждый день.', 'Аристотель', 'Привычки', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_131', 'Лучший способ предсказать будущее — создать его.', 'Питер Друкер', 'Ответственность', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_132', 'Смелость быть собой — самый красивый выбор.', 'Неизвестный автор', 'Аутентичность', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_133', 'Не держите в себе чувства — они становятся грузом.', 'Неизвестный автор', 'Освобождение', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_134', 'Ни одна ночь не длится вечно.', 'Неизвестный автор', 'Надежда', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_135', 'Запишите свои мысли — так они перестанут быть хаосом.', 'Неизвестный автор', 'Практики', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_136', 'Ваша история уже вдохновляет — просто продолжайте.', 'Неизвестный автор', 'Вдохновение', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_137', 'Мир реагирует на вашу внутреннюю гармонию.', 'Неизвестный автор', 'Осознанность', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_138', 'Иногда нужно расплакаться, чтобы освободить место для улыбки.', 'Неизвестный автор', 'Эмоции', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_139', 'Сначала примите себя, потом улучшайте.', 'Неизвестный автор', 'Саморазвитие', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_140', 'Прислушайтесь к паузам — в них ответы.', 'Неизвестный автор', 'Осознанность', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_141', 'Спокойный ум — лучшая защита от хаоса.', 'Неизвестный автор', 'Сила', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_142', 'Не нужно спешить. Нужно быть, чувствовать и понимать.', 'Неизвестный автор', 'Осознанность', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_143', 'Там, где есть искренность, есть доверие.', 'Неизвестный автор', 'Отношения', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_144', 'Ищите радость в простом, и она станет вашей привычкой.', 'Неизвестный автор', 'Счастье', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_145', '«Самый лучший способ предсказать будущее — создать его».', 'Питер Друкер', 'Лидерство', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_146', '«Успех — это способность двигаться от неудачи к неудаче, не теряя энтузиазма».', 'Уинстон Черчилль', 'Успех', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_147', '«Мы — это то, что делаем постоянно. Совершенство — не действие, а привычка».', 'Аристотель', 'Привычки', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_148', '«Дорогу осилит идущий».', 'Овидий', 'Настойчивость', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_149', '«Когда есть “зачем”, можно выдержать почти любое “как”».', 'Виктор Франкл', 'Смысл', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_150', '«Всё, что нас не убивает, делает нас сильнее».', 'Фридрих Ницше', 'Стойкость', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_151', '«Ваше время ограничено, не тратьте его, живя чужой жизнью».', 'Стив Джобс', 'Аутентичность', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_152', '«Жизнь — это то, что с нами происходит, пока мы строим другие планы».', 'Джон Леннон', 'Осознанность', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_153', '«Гений — это один процент вдохновения и девяносто девять процентов пота».', 'Томас Эдисон', 'Труд', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_154', '«Стань изменением, которое хочешь увидеть в мире».', 'Махатма Ганди', 'Вдохновение', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_155', '«Никогда не поздно стать тем, кем мог бы быть».', 'Джордж Элиот', 'Саморазвитие', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_156', '«Мы становимся тем, о чём думаем большую часть времени».', 'Эрл Найтингейл', 'Мышление', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_157', '«Большинство людей тратит больше сил на обход трудностей, чем на их преодоление».', 'Генри Форд', 'Настойчивость', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_158', '«То, кем вы станете, гораздо важнее того, что вы получите».', 'Джим Рон', 'Ценности', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_159', '«Тот, кто хочет — ищет возможности; тот, кто не хочет — ищет причины».', 'Наполеон Бонапарт', 'Ответственность', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_160', '«Судьба человека — в его собственном характере».', 'Геродот', 'Характер', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_161', '«Счастье — это когда то, что ты думаешь, говоришь и делаешь, находится в гармонии».', 'Махатма Ганди', 'Счастье', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_162', '«Не бойтесь совершенства. Вам его не достичь».', 'Сальвадор Дали', 'Саморазвитие', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_163', '«Человек велик не тем, что он имеет, а тем, что он даёт».', 'Виктор Гюго', 'Щедрость', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_164', '«Все думают изменить мир, но никто не думает изменить себя».', 'Лев Толстой', 'Саморазвитие', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_165', '«Красноречивее всего о человеке говорит то, над чем он смеётся».', 'Иоганн Вольфганг Гёте', 'Мышление', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_166', '«Мужество — это сопротивление страху, контроль над страхом, а не отсутствие страха».', 'Марк Твен', 'Смелость', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_167', '«Цель жизни — не в том, чтобы быть на стороне большинства, а в том, чтобы жить согласно своему пониманию долга».', 'Марк Аврелий', 'Ценности', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_168', '«Сначала мы формируем привычки, затем привычки формируют нас».', 'Джон Драйден', 'Привычки', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_169', '«Инвестиции в знания приносят наибольший доход».', 'Бенджамин Франклин', 'Обучение', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_170', '«Если вы думаете, что образование дорого, попробуйте невежество».', 'Дерек Бок', 'Обучение', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_171', '«Ничего великого нельзя достигнуть без страсти».', 'Георг Гегель', 'Вдохновение', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_172', '«Лишь тот, кто берёт на себя ответственность, может быть свободным».', 'Фридрих фон Хайек', 'Свобода', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_173', '«Если ты рожден без крыльев — не мешай им расти».', 'Коко Шанель', 'Аутентичность', strftime('%s', 'now') * 1000);
INSERT INTO quotes (id, text, author, category, created_at) VALUES ('quote_174', '«Самый трудный шаг — решиться действовать, остальное — только вопрос настойчивости».', 'Амелия Эрхарт', 'Смелость', strftime('%s', 'now') * 1000);